import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Container, Typography, Button, CircularProgress,
  Snackbar, Alert, IconButton, Box, Input
} from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import bg from './.';

const Upload = () => {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [copied, setCopied] = useState(false);

  const navigate = useNavigate();

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async (event) => {
    event.preventDefault();
    if (!file) {
      setError('Please select a file.');
      setOpenSnackbar(true);
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setResponse(res.data);
      setError(null);
      setOpenSnackbar(true);
    } catch (err) {
      setError('Failed to upload file.');
      setResponse(null);
      setOpenSnackbar(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCloseSnackbar = () => setOpenSnackbar(false);

  const handleCopy = () => {
    if (response) {
      navigator.clipboard.writeText(JSON.stringify(response, null, 2))
        .then(() => {
          setCopied(true);
          setTimeout(() => setCopied(false), 2000);
        })
        .catch((err) => console.error('Failed to copy:', err));
    }
  };

  const handleProveMRCClick = () => navigate('/prove-mrc');

  return (
    <Box
      sx={{ height: '100vh', width: '100vw', display: 'flex', justifyContent: 'center', alignItems: 'center' }}
    >
      <Container
        component="main"
        maxWidth="md"
        sx={{
          marginTop: '4rem',
          padding: '3rem',
          background: 'linear-gradient(135deg, #e0f7fa, #80deea)',
          borderRadius: '15px',
          boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
        }}
      >
        <Typography
          variant="h3"
          gutterBottom
          sx={{
            color: '#004d40',
            fontWeight: 'bold',
            textAlign: 'center',
            marginBottom: '2rem',
          }}
        >
          Upload Your Document
        </Typography>

        <form onSubmit={handleUpload} noValidate>
          <Input
            type="file"
            onChange={handleFileChange}
            fullWidth
            sx={{
              display: 'block',
              marginBottom: '2rem',
              color: '#004d40',
              '&::file-selector-button': {
                backgroundColor: '#004d40',
                color: '#fff',
                padding: '8px 16px',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                marginRight: '10px',
                '&:hover': {
                  backgroundColor: '#00796b',
                },
              },
            }}
          />

          <Button
            type="submit"
            fullWidth
            variant="contained"
            disabled={loading}
            sx={{
              marginTop: '2rem',
              padding: '1rem',
              fontSize: '1.1rem',
              fontWeight: 'bold',
              background: 'linear-gradient(45deg, #00897b, #26a69a)',
              color: '#fff',
              borderRadius: '10px',
              boxShadow: '0 8px 20px rgba(0, 128, 128, 0.3)',
              '&:hover': {
                background: 'linear-gradient(45deg, #00796b, #004d40)',
              },
            }}
          >
            {loading ? <CircularProgress size={24} /> : 'Upload'}
          </Button>
        </form>

        {response && (
          <>
            <Box
              sx={{
                marginTop: '3rem',
                padding: '2rem',
                border: '1px solid #ddd',
                borderRadius: '15px',
                backgroundColor: '#ffffff',
                boxShadow: '0 8px 25px rgba(0, 0, 0, 0.15)',
                maxHeight: '400px',
                overflowY: 'auto',
                position: 'relative',
              }}
            >
              <IconButton
                onClick={handleCopy}
                color="primary"
                sx={{ position: 'absolute', top: 8, right: 8 }}
              >
                <ContentCopyIcon />
              </IconButton>
              <Typography
                variant="body1"
                color="textSecondary"
                sx={{
                  fontSize: '1rem',
                  color: '#37474f',
                  wordWrap: 'break-word',
                  whiteSpace: 'pre-wrap',
                }}
              >
                <pre>{JSON.stringify(response, null, 2)}</pre>
              </Typography>
              {copied && (
                <Snackbar
                  open={copied}
                  autoHideDuration={2000}
                  onClose={() => setCopied(false)}
                  message="Copied to clipboard"
                />
              )}
            </Box>

            <Button
              fullWidth
              variant="contained"
              color="secondary"
              sx={{
                marginTop: '2rem',
                padding: '1rem',
                fontWeight: 'bold',
                fontSize: '1.1rem',
                backgroundColor: '#ff4081',
                borderRadius: '10px',
                boxShadow: '0 6px 20px rgba(255, 64, 129, 0.3)',
                '&:hover': {
                  backgroundColor: '#f50057',
                },
              }}
              onClick={handleProveMRCClick}
            >
              Prove MRC (Machine Readable Code)
            </Button>
          </>
        )}

        {error && (
          <Snackbar
            open={openSnackbar}
            autoHideDuration={6000}
            onClose={handleCloseSnackbar}
          >
            <Alert onClose={handleCloseSnackbar} severity="error">
              {error}
            </Alert>
          </Snackbar>
        )}
      </Container>
    </Box>
  );
};

export default Upload;
