import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Container, Typography, Button, TextField, CircularProgress,
  Snackbar, Alert, IconButton, Box
} from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

const Upload = () => {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [copied, setCopied] = useState(false);

  const navigate = useNavigate();

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async (event) => {
    event.preventDefault();
    if (!file) {
      setError('Please select a file.');
      setOpenSnackbar(true);
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setResponse(res.data);
      setError(null);
      setOpenSnackbar(true);
    } catch (err) {
      setError('Failed to upload file.');
      setResponse(null);
      setOpenSnackbar(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCloseSnackbar = () => setOpenSnackbar(false);

  const handleCopy = () => {
    if (response) {
      navigator.clipboard.writeText(JSON.stringify(response, null, 2))
        .then(() => {
          setCopied(true);
          setTimeout(() => setCopied(false), 2000);
        })
        .catch((err) => console.error('Failed to copy:', err));
    }
  };

  const handleProveMRCClick = () => navigate('/prove-mrc');

  return (
    <Container component="main" maxWidth="md" sx={{ marginTop: '3rem', padding: '2rem', backgroundColor: '#f4f6f8', borderRadius: '10px', boxShadow: '0 8px 30px rgba(0,0,0,0.1)' }}>
      <Typography variant="h3" gutterBottom sx={{ color: '#3f51b5', fontWeight: 'bold', textAlign: 'center' }}>
        Upload Your Document
      </Typography>

      <form onSubmit={handleUpload} noValidate>
        <TextField
          type="file"
          onChange={handleFileChange}
          fullWidth
          margin="normal"
          variant="outlined"
          helperText="Choose a file to upload"
          sx={{
            border: '2px dashed #3f51b5',
            borderRadius: '8px',
            padding: '1rem',
            '& input': { display: 'none' },
            '& label': { color: '#3f51b5', cursor: 'pointer', fontWeight: '500' },
          }}
          InputLabelProps={{ shrink: true }}
        />

        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          disabled={loading}
          sx={{
            marginTop: '2rem',
            padding: '1rem',
            fontSize: '1rem',
            backgroundColor: '#3f51b5',
            '&:hover': { backgroundColor: '#303f9f' }
          }}
        >
          {loading ? <CircularProgress size={24} /> : 'Upload'}
        </Button>
      </form>

      {response && (
        <>
          <Box
            sx={{
              marginTop: '2rem',
              padding: '2rem',
              border: '1px solid #ddd',
              borderRadius: '10px',
              backgroundColor: '#fff',
              boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
              maxHeight: '400px',
              overflowY: 'auto',
              position: 'relative',
            }}
          >
            <IconButton
              onClick={handleCopy}
              color="primary"
              sx={{ position: 'absolute', top: 8, right: 8 }}
            >
              <ContentCopyIcon />
            </IconButton>
            <Typography variant="body1" color="textSecondary">
              <pre>{JSON.stringify(response, null, 2)}</pre>
            </Typography>
            {copied && (
              <Snackbar
                open={copied}
                autoHideDuration={2000}
                onClose={() => setCopied(false)}
                message="Copied to clipboard"
              />
            )}
          </Box>

          <Button
            fullWidth
            variant="contained"
            color="secondary"
            sx={{ marginTop: '2rem', padding: '1rem', backgroundColor: '#f50057', '&:hover': { backgroundColor: '#c51162' } }}
            onClick={handleProveMRCClick}
          >
            Prove MRC (Machine Readable Code)
          </Button>
        </>
      )}

      {error && (
        <Snackbar
          open={openSnackbar}
          autoHideDuration={6000}
          onClose={handleCloseSnackbar}
        >
          <Alert onClose={handleCloseSnackbar} severity="error">
            {error}
          </Alert>
        </Snackbar>
      )}
    </Container>
  );
};

export default Upload;
