import React, { useState, useRef } from 'react';
import axios from 'axios';
import { Container, Typography, Button, TextField, CircularProgress, Snackbar, Alert, Box, IconButton } from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

const Upload = () => {
    const [file, setFile] = useState(null);
    const [response, setResponse] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const [openSnackbar, setOpenSnackbar] = useState(false);

    // Ref to the output container
    const outputRef = useRef(null);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async (event) => {
        event.preventDefault();

        if (!file) {
            setError('Please select a file.');
            setOpenSnackbar(true);
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        setLoading(true);
        try {
            const res = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setResponse(res.data);
            setError(null);
            setOpenSnackbar(true);
        } catch (err) {
            setError('Failed to upload file.');
            setResponse(null);
            setOpenSnackbar(true);
        } finally {
            setLoading(false);
        }
    };

    const handleCloseSnackbar = () => {
        setOpenSnackbar(false);
    };

    const handleCopy = () => {
        if (outputRef.current) {
            const range = document.createRange();
            range.selectNode(outputRef.current);
            window.getSelection().removeAllRanges();
            window.getSelection().addRange(range);
            document.execCommand('copy');
            window.getSelection().removeAllRanges();
            setOpenSnackbar(true);
            setError('Copied to clipboard!');
        }
    };

    const formatJsonToText = (json) => {
        if (typeof json === 'object') {
            let formattedText = JSON.stringify(json, null, 2); // Indent with 2 spaces
            formattedText = formattedText.replace(/\n/g, '\n'); // Preserve newlines
            return formattedText;
        }
        return json;
    };

    return (
        <Container component="main" maxWidth="xs" style={{ marginTop: '2rem' }}>
            <Typography variant="h4" gutterBottom>
                Upload Document
            </Typography>
            <form onSubmit={handleUpload} noValidate>
                <TextField
                    type="file"
                    onChange={handleFileChange}
                    fullWidth
                    margin="normal"
                    variant="outlined"
                    helperText="Choose a file to upload"
                />
                <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                    disabled={loading}
                    style={{ marginTop: '1rem' }}
                >
                    {loading ? <CircularProgress size={24} /> : 'Upload'}
                </Button>
            </form>
            {response && (
                <Box
                    ref={outputRef}
                    style={{
                        position: 'relative',
                        marginTop: '1rem',
                        padding: '1rem',
                        border: '1px solid #ddd',
                        borderRadius: '4px',
                        maxHeight: '300px',
                        overflowY: 'auto',
                        backgroundColor: '#f9f9f9',
                        whiteSpace: 'pre-wrap', // Preserve whitespace and newlines
                        fontFamily: 'monospace', // Monospace font for better readability
                    }}
                >
                    <IconButton
                        onClick={handleCopy}
                        style={{
                            position: 'absolute',
                            top: '10px',
                            right: '10px',
                        }}
                    >
                        <ContentCopyIcon />
                    </IconButton>
                    <Typography variant="body1" color="textSecondary">
                        <pre>{formatJsonToText(response)}</pre>
                    </Typography>
                </Box>
            )}
            {error && (
                <Snackbar
                    open={openSnackbar}
                    autoHideDuration={6000}
                    onClose={handleCloseSnackbar}
                >
                    <Alert onClose={handleCloseSnackbar} severity={error === 'Copied to clipboard!' ? 'success' : 'error'}>
                        {error}
                    </Alert>
                </Snackbar>
            )}
        </Container>
    );
};

export default Upload;
